import React, { useEffect, useState } from 'react';
import './PokemonInfo.css';

function PokemonInfo() {
  const [pokemonList, setPokemonList] = useState([]);
  const [searchValue, setSearchValue] = useState('');
  const [searchedPokemon, setSearchedPokemon] = useState(null);

  useEffect(() => {
    const fetchPokemonData = async () => {
      try {
        const response = await fetch('https://pokeapi.co/api/v2/pokemon?limit=100');
        const data = await response.json();
        setPokemonList(data.results);
      } catch (error) {
        console.log('Error fetching Pokémon data:', error);
      }
    };

    fetchPokemonData();
  }, []);

  const fetchPokemonDetails = async (url) => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      return data;
    } catch (error) {
      console.log('Error fetching Pokémon details:', error);
    }
  };

  const handleSearch = async () => {
    if (searchValue.trim() === '') {
      return;
    }

    try {
      const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${searchValue.toLowerCase()}`);
      const data = await response.json();
      setSearchedPokemon(data);
    } catch (error) {
      console.log('Error fetching Pokémon details:', error);
    }
  };

  useEffect(() => {
    const fetchDetails = async () => {
      if (searchedPokemon) {
        const details = await fetchPokemonDetails(searchedPokemon.url);
        setSearchedPokemon((prevSearchedPokemon) => ({
          ...prevSearchedPokemon,
          details: details,
        }));
      }
    };

    fetchDetails();
  }, [searchedPokemon]);

  const handleInputChange = (event) => {
    setSearchValue(event.target.value);
  };

  return (
    <div>
      <nav className="navbar"> 
        <input className="search-input" type="text" placeholder="Search by name" value={searchValue} onChange={handleInputChange} />
        <button className="search-button" onClick={handleSearch}>Search</button>
      </nav>
      {searchedPokemon ? (
        <div className="pokemon-info">
          <div className="pokemon-card">
            <h2>{searchedPokemon.name}</h2>
            <p>ID: {searchedPokemon.details.id}</p>
            <p>Base Experience: {searchedPokemon.details.base_experience}</p>
            <p>Height: {searchedPokemon.details.height}</p>
            <p>Order: {searchedPokemon.details.order}</p>
            <p>Weight: {searchedPokemon.details.weight}</p>
            <img
              src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${searchedPokemon.details.id}.png`}
              alt={searchedPokemon.name}
            />
          </div>
        </div>
      ) : (
        <div className="pokemon-info">
          {pokemonList.length > 0 ? (
            pokemonList.map((pokemon, index) => (
              <div key={index} className="pokemon-card">
                <h2>{pokemon.name}</h2>
                <img
                  src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${index + 1}.png`}
                  alt={pokemon.name}
                />
              </div>
            ))
          ) : (
            <p>Loading Pokémon data...</p>
          )}
        </div>
      )}
    </div>
  );
}

export default PokemonInfo;
