import React, { useEffect, useState } from 'react';
import './PokemonInfo.css';

function PokemonInfo() {
  const [pokemonList, setPokemonList] = useState([]);
  const [searchValue, setSearchValue] = useState('');

  function handleSearchInputChange(event) {
    setSearchValue(event.target.value);
  }

  async function handleSearch() {
    if (searchValue.trim() === '') {
      return;
    }

    try {
      const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${searchValue}`);
      const data = await response.json();
      setPokemonList([{ name: data.name, url: data.url, details: data }]);
    } catch (error) {
      console.log('Error fetching Pokémon details:', error);
    }
  }

  useEffect(() => {
    const fetchPokemonData = async () => {
      try {
        const response = await fetch('https://pokeapi.co/api/v2/pokemon?limit=50');
        const data = await response.json();
        setPokemonList(data.results);
      } catch (error) {
        console.log('Error fetching Pokémon data:', error);
      }
    };

    fetchPokemonData();
  }, []);

  const fetchPokemonDetails = async (url) => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      return data;
    } catch (error) {
      console.log('Error fetching Pokémon details:', error);
    }
  };

  useEffect(() => {
    const fetchDetails = async () => {
      const details = await Promise.all(
        pokemonList.map((pokemon) => fetchPokemonDetails(pokemon.url))
      );
      setPokemonList((prevList) =>
        prevList.map((pokemon, index) => ({
          ...pokemon,
          details: details[index],
        }))
      );
    };

    fetchDetails();
  }, [pokemonList]);

  return (
    <div>
      <div className='navbar'>
        <input
          className="search-input"
          type="text"
          placeholder="Search by ID"
          value={searchValue}
          onChange={handleSearchInputChange}
        />
        <button className="search-button" onClick={handleSearch}>Pesquisar</button>
      </div>
      {pokemonList.length > 0 ? (
        <div className="pokemon-info">
          {pokemonList.map((pokemon, index) => (
            <div key={index} className="pokemon-card">
              <h2>{pokemon.name}</h2>
              {pokemon.details && (
                <React.Fragment>
                  <p>ID: {pokemon.details.id}</p>
                  <p>Base Experience: {pokemon.details.base_experience}</p>
                  <p>Height: {pokemon.details.height}</p>
                  <p>Order: {pokemon.details.order}</p>
                  <p>Weight: {pokemon.details.weight}</p>
                </React.Fragment>
              )}
              <img
                src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${
                  index + 1
                }.png`}
                alt={pokemon.name}
              />
            </div>
          ))}
        </div>
      ) : (
        <p>Loading Pokémon data...</p>
      )}
    </div>
  );
}

export default PokemonInfo;
