import React, { useEffect, useState } from 'react';
import './PokemonInfo.css';

function PokemonInfo() {
  const [pokemonList, setPokemonList] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [pokemonData, setPokemonData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPokemonData = async () => {
      try {
        const response = await fetch('https://pokeapi.co/api/v2/pokemon?limit=100');
        const data = await response.json();
        setPokemonList(data.results);
      } catch (error) {
        console.log('Error fetching Pokémon data:', error);
      }
    };

    fetchPokemonData();
  }, []);

  const fetchPokemonDetails = async (url) => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      return data;
    } catch (error) {
      console.log('Error fetching Pokémon details:', error);
    }
  };

  useEffect(() => {
    const fetchDetails = async () => {
      const details = await Promise.all(
        pokemonList.map((pokemon) => fetchPokemonDetails(pokemon.url))
      );
      setPokemonList((prevList) =>
        prevList.map((pokemon, index) => ({
          ...pokemon,
          details: details[index],
        }))
      );
    };

    fetchDetails();
  }, [pokemonList]);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${searchTerm}`);
      if (response.ok) {
        const data = await response.json();
        setPokemonData(data);
        setError(null);
      } else {
        setPokemonData(null);
        setError('Pokémon not found');
      }
    } catch (error) {
      console.error('Error:', error);
      setPokemonData(null);
      setError('Error fetching Pokémon');
    }
    setSearchTerm('');
  };

  return (
    <div className="pokemon-info">
      <nav className="navbar">
        <form className="search-form" onSubmit={handleSearchSubmit}>
          <input
            className="search-input"
            type="text"
            placeholder="Digite o nome do Pokémon"
            value={searchTerm}
            onChange={handleSearchChange}
          />
          <button className="search-button" type="submit">Pesquisar</button>
        </form>
        {pokemonData && (
          <div className="card">
            <h2>{pokemonData.name}</h2>
            <p>ID: {pokemonData.id}</p>
            <p>Base Experience: {pokemonData.base_experience}</p>
            <p>Height: {pokemonData.height}</p>
            <p>Order: {pokemonData.order}</p>
            <p>Weight: {pokemonData.weight}</p>
          </div>
        )}
        {error && <p>{error}</p>}
      </nav>

      {pokemonList.length > 0 ? (
        <div className="pokemon-cards">
          {pokemonList.map((pokemon, index) => (
            <div key={index} className="pokemon-card">
              <h2>{pokemon.name}</h2>
              {pokemon.details && (
                <React.Fragment>
                  <p>ID: {pokemon.details.id}</p>
                  <p>Base Experience: {pokemon.details.base_experience}</p>
                  <p>Height: {pokemon.details.height}</p>
                  <p>Order: {pokemon.details.order}</p>
                  <p>Weight: {pokemon.details.weight}</p>
                </React.Fragment>
              )}
              <img
                src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${index + 1}.png`}
                alt={pokemon.name}
              />
            </div>
          ))}
        </div>
      ) : (
        <p>Loading Pokémon data...</p>
      )}
    </div>
  );
}

export default PokemonInfo;
