import React, { useState } from 'react';
import './Navbar.css';

function Navbar() {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    searchPokemon(searchTerm);
    setSearchTerm('');
  };

  const searchPokemon = (name) => {
    // Faz uma requisição à API do Pokémon usando o nome do Pokémon
    fetch(`https://pokeapi.co/api/v2/pokemon/${name}`)
      .then(response => response.json())
      .then(data => {
        // Faça o que você quiser com os dados do Pokémon recebidos
        console.log('Dados do Pokémon:', data);
      })
      .catch(error => {
        // Lida com erros da requisição
        console.log('Ocorreu um erro na busca do Pokémon:', error);
      });
  };

  return (
    <nav className="navbar">
      <form className="search-form" onSubmit={handleSearchSubmit}>
        <input
          className="search-input"
          type="text"
          placeholder="Digite o nome do Pokémon"
          value={searchTerm}
          onChange={handleSearchChange}
        />
        <button className="search-button" type="submit">Pesquisar</button>
      </form>
    </nav>
  );
}

export default Navbar;
