import React, { useEffect, useState } from 'react';
import './PokemonInfo.css';

function PokemonInfo() {
  const [pokemonList, setPokemonList] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);
  const qtdPokemon = 1000;

  useEffect(() => {
    const fetchPokemonData = async () => {
      try {
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon?limit=${itemsPerPage}`);
        const data = await response.json();
        setPokemonList(data.results);
      } catch (error) {
        console.log('Error fetching Pokémon data:', error);
      }
    };

    fetchPokemonData();
  }, [itemsPerPage]);

  const fetchPokemonDetails = async (url) => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      return data;
    } catch (error) {
      console.log('Error fetching Pokémon details:', error);
    }
  };

  useEffect(() => {
    const fetchDetails = async () => {
      const startIndex = (currentPage - 1) * itemsPerPage;
      const endIndex = currentPage * itemsPerPage;
      const pokemonSubset = pokemonList.slice(startIndex, endIndex);

      const details = await Promise.all(
        pokemonSubset.map((pokemon) => fetchPokemonDetails(pokemon.url))
      );
      setPokemonList((prevList) =>
        prevList.map((pokemon, index) => ({
          ...pokemon,
          details: details[index],
        }))
      );
    };

    fetchDetails();
  }, [pokemonList, currentPage, itemsPerPage]);

  const totalPages = Math.ceil(pokemonList.length / itemsPerPage);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  return (
    <div className="pokemon-info">
      {pokemonList.length > 0 ? (
        <div className="pokemon-info">
          {pokemonList.map((pokemon, index) => (
            <div key={index} className="pokemon-card">
              <h2>{pokemon.name}</h2>
              {pokemon.details && (
                <React.Fragment>
                  <p>ID: {pokemon.details.id}</p>
                  <p>Base Experience: {pokemon.details.base_experience}</p>
                  <p>Height: {pokemon.details.height}</p>
                  <p>Order: {pokemon.details.order}</p>
                  <p>Weight: {pokemon.details.weight}</p>
                </React.Fragment>
              )}
              <img
                src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${
                  index + 1
                }.png`}
                alt={pokemon.name}
              />
            </div>
          ))}
          <div className="pagination">
            {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
              <button
                key={page}
                onClick={() => handlePageChange(page)}
                className={currentPage === page ? 'active' : ''}
              >
                {page}
              </button>
            ))}
            <button onClick={handleNextPage} disabled={currentPage >= totalPages}>
              Next Page
            </button>
          </div>
        </div>
      ) : (
        <p>Loading Pokémon data...</p>
      )}
    </div>
  );
}

export default PokemonInfo;
