import React from 'react';
import { useParams, Link } from 'react-router-dom';

function PokemonDetails() {
  const { id } = useParams();

  // Fetch additional details for the specific Pokemon based on the ID here
  // You can use a similar approach as you did in the Navbar component to fetch data

  return (
    <div>
      {/* Display the details of the Pokemon here */}
      <h2>Pokemon Details - ID: {id}</h2>

      <Link to="/">Back to Search</Link>
    </div>
  );
}

export default PokemonDetails;
