import React, { useEffect, useState } from 'react';
import './PokemonInfo.css';

function PokemonInfo() {
  const [pokemonList, setPokemonList] = useState([]);
  const [searchText, setSearchText] = useState('');

  useEffect(() => {
    const fetchPokemonData = async () => {
      try {
        const response = await fetch('https://pokeapi.co/api/v2/pokemon?limit=1000');
        const data = await response.json();
        setPokemonList(data.results);
      } catch (error) {
        console.log('Error fetching Pokémon data:', error);
      }
    };

    fetchPokemonData();
  }, []);

  const fetchPokemonDetails = async (url) => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      return data;
    } catch (error) {
      console.log('Error fetching Pokémon details:', error);
    }
  };

  useEffect(() => {
    const fetchDetails = async () => {
      const details = await Promise.all(
        pokemonList.map((pokemon) => fetchPokemonDetails(pokemon.url))
      );
      setPokemonList((prevList) =>
        prevList.map((pokemon, index) => ({
          ...pokemon,
          details: details[index],
        }))
      );
    };

    fetchDetails();
  }, [pokemonList]);

  const handleSearchChange = (event) => {
    setSearchText(event.target.value);
  };

  const filteredPokemonList = pokemonList.filter((pokemon) =>
    pokemon.name.toLowerCase().includes(searchText.toLowerCase())
  );

  return (
    <div className="pokemon-info">
      <div className="search-container">
        <input
          type="text"
          placeholder="Search Pokémon"
          value={searchText}
          onChange={handleSearchChange}
        />
      </div>
      {filteredPokemonList.length > 0 ? (
        <div>
          {filteredPokemonList.map((pokemon, index) => (
            <div key={index} className="pokemon-card">
              <h2>{pokemon.name}</h2>
              {pokemon.details && (
                <React.Fragment>
                  <p>ID: {pokemon.details.id}</p>
                  <p>Base Experience: {pokemon.details.base_experience}</p>
                  <p>Height: {pokemon.details.height}</p>
                  <p>Is Default: {pokemon.details.is_default ? 'Yes' : 'No'}</p>
                  <p>Order: {pokemon.details.order}</p>
                  <p>Weight: {pokemon.details.weight}</p>
                </React.Fragment>
              )}
              <img
                src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${
                  index + 1
                }.png`}
                alt={pokemon.name}
              />
            </div>
          ))}
        </div>
      ) : (
        <p>No Pokémon found.</p>
      )}
    </div>
  );
}

export default PokemonInfo;
